import { Button} from "react-bootstrap"
import PropTypes from "prop-types";
import styles from "./styles";

/**
 * @param {string} action - Action for button.
 * @param {string} children - Text button.
 * @param {('primary'|'secondary')} type - if primary button is color red, else secondary is color white  
 * @param {boolean} disabled - True o false disabled button
 */
const CustomButton = ({ action, children, type, disabled }) =>{

    return(
        <>
            <button
                className={type ?? "primary"}
                onClick={() => action && [action()]}
                disabled={disabled}
                type={type}
            >
                {children}
            </button>
            <style jsx>{`
        div {
            width: 100%;
        }
        button {
            background-color: rgb(0 0 0 / 0.02);
            border: none;
            cursor: pointer;
            color: #000;
            display: inline-block;
            font-family: Arial;
            font-size: 15px;
            font-weight: 500;
            padding: 6px;
            text-decoration: none;
            outline-color: #33bdef36;
        }
        button:hover {
            background:linear-gradient(to bottom,#019ad20f 5%,#33bdef1a 100%);
            background-color:#019ad20f;
        }
        button:active {
            border: solid 1px #c8d3fe;
            position:relative;
            top:1px;
        }
        .primary {
            background-color: red; 
        }
        `}</style>
        </>
    )
}
CustomButton.propTypes = {
    action: PropTypes.func,
    children: PropTypes.string,
    disabled: PropTypes.bool,
    type: PropTypes.oneOf([
        "primary",
        "secondary"
      ])
  };

export default CustomButton;
