import { Button} from "react-bootstrap"
import PropTypes from "prop-types";
import styles from "./styles";

/**
 * @param {string} action - Action for button.
 * @param {string} children - Text button.
 * @param {('primary'|'secondary')} type - if primary button is color red, else secondary is color white  
 * @param {boolean} disabled - True o false disabled button
 */
const CustomButton = ({ action, children, type, disabled }) =>{

    return(
        <>
            <button
                className={type ?? "secondary"}
                onClick={() => action && [action()]}
                disabled={disabled}
                type={type}
            >
                {children}
            </button>
            <style jsx>{`
        div {
            width: 100%;
        }
        button {
            border: none;
            outline-color: none;
            outline: none;
            height: 56px;
            width: 260px;
            left: 0px;
            top: 0px;
            border-radius: 4px;

        }
        button:active {
            border: solid 1px #FFFFFF;
            position:relative;
            top:1px;
        }
        .primary {
            background-color: #BD2626;
            color: #FFFFFF;
        }
        .primary:hover {
            background-color: #bb4e4e;
            color: #FFFFFF;
        }
        .secondary {
            background-color: #FFFFFF;
            background: #3A4056;
        }
        .secondary:hover {
            background-color: #bb4e4e;
            color: #FFFFFF;
        }
        `}</style>
        </>
    )
}
CustomButton.propTypes = {
    action: PropTypes.func,
    children: PropTypes.string,
    disabled: PropTypes.bool,
    type: PropTypes.oneOf([
        "primary",
        "secondary"
      ])
  };

export default CustomButton;
